(274, 'VT000001', 3, 'challenge', '- thành lập Hiệp hội thương hiệu quốc gia\r\n- quảng bá chương trình thương hiệu quốc gia ra quốc tế', NULL, 2, 2015, '2015-08-27 17:31:29'),
(275, 'VT000001', 51, 'after', 'Lớp học có 30  học viên, 13 nữ, 17 nam - từ 06 tỉnh Cần Thơ, An Giang, Vĩnh Long, Sóc Trăng, Trà Vinh, Long An. Đến cuối lớp học 6 ngày đã có 28 học viên hoàn thành- 2 học viên phải nghỉ giữa chừng do công việc cơ quan.', NULL, 4, 0000, '2015-08-27 17:38:47'),
(276, 'VT000001', 51, 'after1month', 'Các học viên duy trì đều đặn việc gặp mặt, liên lạc và trao đổi lẫn nhau qua nhiều kênh.', NULL, 4, 0000, '2015-08-27 17:38:47'),
(277, 'VT000001', 51, 'empirical', '28 học viên đã kết thúc khóa học 6 ngày theo đúng thiết kế của UNCTAD với rất nhiều kinh nghiệm và bài học quý, đặc biệt yêu cầu cần liên kết, hợp tác để tạo sức mạnh tổng hợp. Thành phần của các học viên khá đa dạng gồm đại diện các đơn vị quản lý tại địa phương như Trung tâm Xúc tiến Thương mại Du lịch Cần Thơ, VCCI Cần Thơ còn gồm rất nhiều doanh nghiệp trong các lĩnh vực thủy sản, lúa gạo, cơ khí, du lịch, vận tải (logistics), ngân hàng, nhựa, dịch vụ truyền thông (xây dựng website,…)… và sẽ cần duy trì sự kết nối mạng lưới trong thời gian tới để hỗ trợ Chương trình triển khai tốt hơn các hoạt động của mình.', NULL, 4, 0000, '2015-08-27 17:38:47'),
(278, 'VT000001', 51, 'challenge', '- Duy trì sự liên kết giữa các học viên sau khóa học với những hình thức khác nhau.', NULL, 4, 0000, '2015-08-27 17:38:47'),
(279, 'VT000001', 77, 'after', 'Ban quản lý Chương trình đã phối hợp với Phòng thương mại và công nghiệp Việt Nam – Chi nhánh Đà nẵng (VCCI Đà Nẵng) tổ chức Khóa đào tạo Doanh nhân Empretec 18 Đà Nẵng từ ngày 20-25/7/2015.\r\nKết quả, 20 học viên đã hoàn thành và nhận chứng chỉ từ Ban tổ chức. Các học viên và đơn vị phối họp tổ chức đánh giá cao chất lượng của Khóa học.', NULL, 3, 2015, '2015-08-27 18:00:18'),
